elements.expression:
filters
| essql 
  query="SELECT  
carrier \"Carrier\", 
full_flight_number \"Flight\", 
direction \"Direction\",
origin \"Origin\",
destination \"Destination\", 
full_utc_etd_timestamp \"Departure Date\",
passenger_count  \"Passenger Count\",
apis_count \"APIS Count\", 
pnr_count \"PNR Count\"
FROM \"message_count*\"
ORDER BY full_utc_etd_timestamp desc, carrier, direction "
| mapColumn "APIS Count" fn={if {getCell "APIS Count" | eq null} then="0" else={getCell "APIS Count"}}
| mapColumn "PNR Count" fn={if {getCell "PNR Count" | eq null} then="0" else={getCell "PNR Count"}}
| table 
  font={font family="'Open Sans', Helvetica, Arial, sans-serif" size=14 align="center" color="#000000" weight="normal" underline=false italic=false}
| render 
  css=".canvasDataTable__th { text-decoration: underline; background-color: #4863A0; color: white;}
.canvasDataTable__tbody>:nth-child(even) {
background-color: #D1D0CE;
}
.canvasDataTable__tbody>:nth-child(odd) {
background-color: #FFFFFF;
}"

