elements.expression:
filters
| essql 
  query="SELECT 
carrier \"Carrier\", 
full_flight_number \"Flight\", 
message_type \"Message Type\",
msg_trans_comp_hrs \"Compliance
Time Hrs\",
direction \"Direction\",
origin \"Origin\",
destination \"Destination\",
passenger_count \"Passenger Count\"




FROM \"message_delivery*\"
ORDER BY full_utc_etd_timestamp desc, carrier, full_flight_number,message_type,msg_trans_comp_hrs"
| mapColumn "Transmission Date" fn={date | formatdate "YYYY-MM-DD HH:mm:ss"}
| mapColumn "Departure Date" fn={date | formatdate "YYYY-MM-DD HH:mm:ss"}
| table perPage=25 showHeader=true 
  font={font size=14 family="'Open Sans', Helvetica, Arial, sans-serif" color="#000000" align="left"}
| render 
  css=".canvasDataTable__th { text-decoration: underline; background-color: #4863A0; color: white;}
.canvasDataTable__tbody>:nth-child(even) {
background-color: #D1D0CE;
}
.canvasDataTable__tbody>:nth-child(odd) {
background-color: #FFFFFF;
}"

filters
| demodata
| markdown "Message Delivery Compliance Report" 
  font={font family="'Open Sans', Helvetica, Arial, sans-serif" size=18 align="center" color="#FFFFFF" weight="bold" underline=false italic=false}
| render css=".canvasRenderEl {
background-color: #4863A0;
}"
